const gulp=require("gulp"),cleanCSS=require("gulp-clean-css"),browserSync=require("browser-sync"),concat=require("gulp-concat"),minify=require("gulp-minify");function minifyCss(){return gulp.src("./*.css").pipe(concat("style.min.css")).pipe(cleanCSS()).pipe(gulp.dest("./"))}function minifyJs(){return gulp.src("./**/*.js").pipe(minify()).pipe(gulp.dest("./js"))}function watch(){browserSync.init({server:{baseDir:"./"}}),gulp.watch("./src/css/**/*.css",minifyCss),gulp.watch("./src/js/**/*.js",minifyJs),gulp.watch("./www/").on("change",browserSync.reload)}exports.watch=watch,exports.minifyCss=minifyCss,exports.minifyJs=minifyJs;